'use client'

import { useState } from "react"
import { cn } from "../../lib/utils"
import { Input } from "../../components/ui/input"
import { Textarea } from "../../components/ui/textarea"
import { CornerRightUp, Mic } from "lucide-react"
import { useAutoResizeTextarea } from "../../hooks/use-auto-resize-textarea"

export default function AIInput_01() {
    const { textareaRef, adjustHeight } = useAutoResizeTextarea({ minHeight: 50, maxHeight: 200 });
    const [inputValue, setInputValue] = useState("")
    const handleReset = () => {
        setInputValue("")
        adjustHeight(true)
    }
    return (

        <div className="w-full py-4">
            <div className="relative max-w-xl w-full mx-auto ">
                <Textarea
                    id="ai-input-01"
                    placeholder="Type your message here..."
                    className={
                        cn("max-w-xl bg-black/5 dark:bg-white/5 rounded-3xl pl-6 pr-16",
                            "placholder:text-black/50 dark:placeholder:text-white/50",
                            "border-none ring-black/20 dark:ring-white/20",
                            "text-black dark:text-white text-wrap",
                            "overflow-y-auto resize-none",
                            "focus-visible:ring-0 focus-visible:ring-offset-0",
                            "transition-[height] duration-100 ease-out",
                            "leading-[1.2] py-[16px]",
                            "min-h-[50px]",
                            "max-h-[200px]",
                        )}
                    ref={textareaRef}
                    value={inputValue}
                    onChange={(e) => {
                        setInputValue(e.target.value)
                        adjustHeight()
                    }}
                    onKeyDown={(e) => {
                        if (e.key === "Enter" && !e.shiftKey) {
                            e.preventDefault()
                            handleReset()
                        }
                    }}
                />
                <div className={cn(
                    "absolute top-1/2 translation-y-1/2 rounded-xl bg-black/5 dark:bg-white/5 px-1 py-1 transition-all duration-200",
                    inputValue ? "right-10" : "right-3",
                )}>
                    <Mic className="w-4 h-4 text-black/70 dark:text-white/70" />
                </div>

                <button
                    className={cn(
                        "absolute top-1/2 translation-y-1/2 rounded-xl bg-black/5 dark:bg-white/5 px-2 py-1 transition-all duration-700",
                        inputValue ? "block right-3 animate-slide-in cursor-pointer" : "hidden"
                    )}
                >
                    <CornerRightUp className="w-4 h-4 text-black/70 dark:text-white/70" />
                </button>
            </div>

        </div >
    )

}

import { useCallback, useEffect, useRef, useState } from "react"


interface useAutoResizeTextareaProps {
    minHeight: number;
    maxHeight?: number;
}

export function useAutoResizeTextarea({ minHeight, maxHeight }: useAutoResizeTextareaProps) {
    const textareaRef = useRef<HTMLTextAreaElement>(null)
    const adjustHeight = useCallback(
        (reset?: boolean) => {
            const textarea = textareaRef.current;
            if (!textarea) return;
            if (reset) {
                textarea.style.height = `${minHeight}px`
                return;
            }

            textarea.style.height = `${minHeight}px`;

            const newHeight = Math.max(
                minHeight,
                Math.min(
                    textarea.scrollHeight,
                    maxHeight ?? Number.POSITIVE_INFINITY
                )
            );

            textarea.style.height = `${newHeight}px`;

        }, [maxHeight, minHeight]


    );

    useEffect(() => {
        // set initial height
        const textarea = textareaRef.current;
        if (textarea) {
            textarea.style.height = `${minHeight}px`;
        }
    }, [minHeight])
    return {textareaRef, adjustHeight};

}
